document.addEventListener('contextmenu', event => event.preventDefault());
$(document).ready(function() {
	$('#fornovymd5, #foreditmd5').on('input', checkPasswordStrength);
	$("#error-info").show();
	setTimeout(function() {
		$("#error-info").animate({
			opacity: 0
		}, 500).slideUp(400);
	}, 5000);
	$('[title]').tooltip();
});
function checkPasswordStrength() {
	var number = /([0-9])/;
	var alphabets = /([a-zA-Z])/;
	var special_characters = /([~,!,@,#,$,%,^,&,*,-,_,+,=,?,>,<])/;
	var password = $('#fornovymd5, #foreditmd5').val().trim();
	if(password.length === 0) {
		$('#password-strength-status').removeClass().empty();
	} else if(password.length < 6) {
		$('#password-strength-status').removeClass().addClass('weak-password').html("Weak! (Should be at least 6 characters.)");
	} else {
		if(password.match(number) && password.match(alphabets) && password.match(special_characters)) {
			$('#password-strength-status').removeClass().addClass('strong-password').html("Strong!");
		} else {
			$('#password-strength-status').removeClass().addClass('medium-password').html("Medium! (Should include alphabets, numbers, and special characters.)");
		}
	}
}